import styled from 'styled-components';


export const HiddenDiv=styled.div`
width: 260px;
height: 60px;
background-color: violet;
border-radius: 0 0 8px 8px;
display: flex;
flex-direction: column;
pad

& h3{

    align-items: center;
    width: 100%;
    font-size: 1em;
    font-weight: 300;
    margin-bottom: 10px;
}
 & option{
    margin: 0;
    padding: 0.375rem 0.75rem;
    width: 100%;border: 0;
    outline: 0;
 }
`
