import styled from 'styled-components';

export const CardContainer = styled.div`
  display: flex;
  width: auto;
  flex-wrap: wrap;
  justify-content: space-between;
  padding: 1rem;
  @media (max-width: 48rem) {
    flex-direction: row;

  }
`;

export const CardDiv = styled.div`
  display: flex;
  border: .0625rem solid #e3e3e3;
  margin-top: 1.875rem;
  flex-direction: column;
  height: 21rem;
  width: 9.6875rem;
  position: relative;
  border: .0625rem solid #e3e3e3;
  border-radius: .5rem;
  box-shadow: 0 .125rem .5rem rgb(0 0 0 / 10%);
  @media (max-width: 48rem) {
    flex-direction: row;
    width: 100%;
    height: 8.8125rem;
  }
`;

export const CardImage = styled.img`
  background-image: url(${(props) => props.img});
  background-size: cover;
  width: 100%;
  height: 14.75rem;
  min-height: 14.75rem;
  border-radius: .5rem .5rem 0 0;
  margin: 0 0 .5625rem 0;
  background-position: center;
  background-repeat: no-repeat;
  @media (max-width: 48rem) {
    flex-direction: row;
    height: 8.8125rem;
    width: 5.875rem;
    min-height: 8.8125rem;
    border-radius: .5rem 0 0 0;
  }
`;

export const CardInfo = styled.div`
  flex-direction: column;
  width: 100%;
  overflow-wrap: break-word;
  flex-wrap: wrap;
  word-wrap: normal;
  @media (max-width: 48rem) {
    flex-direction: row;
  }
`;

export const Title = styled.h2`
  font-size: .9rem;
  margin-left: .3125rem;
  white-space: normal;
  @media (max-width: 48rem) {
    margin: .625rem;
  }
`;

export const ReleasedDate = styled.p`
  font-size: 8px;
  font-size: 1em;
  margin-left: .3125rem;
  padding: 0;
  color: rgba(0, 0, 0, 0.6);
  @media (max-width: 48rem) {
    margin: .625rem;
  }
`;

export const OverView = styled.p`
  display: none;
  @media (max-width: 48rem) {
    margin: .625rem;
    padding: 0;
    font-size: 0.8em;
    overflow: hidden;
