import styled from 'styled-components';

export const FooterDiv = styled.div`
  background-color: #03233e;
  display: flex;
  flex-direction: column;
  margin-top: 20px;
  padding-bottom: 70px;

  @media (min-width: 768px) {
    justify-content: space-evenly;
    flex-direction: row;
    padding-right: 120px;
    padding: 0;
  }
`;

export const FooterElement = styled.div`
  align-items: center;
  align-self: baseline;
  color: white;
  display: flex;
  flex-direction: column;
  margin: 5px 35px;

  & p {
    font-size: 1em;
    margin: 2px;
  }
  /* & h2 {
    font-family: 'Source Sans Pro', Arial, sans-serif;
    font-size: 17px;
    text-transform: uppercase;
  } */
  @media (min-width: 768px) {
    margin: 70px 0;
  }
`;

export const FooterTitle = styled.h2`
  font-family: 'Source Sans Pro', Arial, sans-serif;
  font-size: 17px;
  text-transform: uppercase;
  margin: 70px 0;
`;

export const FooterLogo = styled.div`
  height: 95px;
  margin-left: 120px;
  margin-top: 30px;
  width: 220px;

  & h3 {
    background-color: white;
    border-radius: 5px;
    border: 2px solid #fff;
    color: #89dcf3;
    font-size: 1em;
    font-weight: bold;
    margin: 33px 10px 0 0px;
    padding: 8px 14px;
    text-align: center;
    @media (max-width: 768px) {
      padding: 8px 14px;
    }
  }
  @media (max-width: 768px) {
    margin: 5px 30px;
  }
`;

export const FooterImage = styled.img.attrs((props) => ({
  src: props.Img,
}))`
  display: none;
  @media (min-width: 768px) {
    margin: 10px 90px;
    width: 120px;
    display: block;
  }
`;
