import React, { useState }  from "react";
import { SearchList, SortDiv ,Arrow, HiddenDiv} from "./styles";

const Sort = () => {
  const [rotate,setRotate]=useState('0');
  const clickHandler=()=>{
  if(rotate==='90deg'){
    setRotate('0deg')
  }else{
setRotate('90deg');

  }}
  return (

      <>  
    <SortDiv>
     <h2>Popular Moives</h2>
      <SearchList onClick={clickHandler}><h2>Sort</h2>   <Arrow rotate={rotate} />  </SearchList>
      {rotate==='90deg'  &&  <HiddenDiv ><select name="sort" onChange={'`sortSubmitHandler`'}>
                <option value="popularity.desc">Popularity Descending</option>
                <option value="popularity.asc">Popularity Ascending</option>
                <option value="vote_average.desc">Rating Descending</option>
                <option value="vote_average.asc">Rating Ascending</option>
                <option value="release_date.desc">
                  Release Date Descending
                </option>
                <option value="release_date.asc">Release Date Ascending</option>
                <option value="original_title.asc">Title (A-Z)</option>
                <option value="original_title.desc">Title (Z-A)</option>
              </select></HiddenDiv>}

      <SearchList> <h2>Filter</h2> <Arrow /></SearchList>
      <SearchList><h2>SearchList</h2> <Arrow  /></SearchList>
    </SortDiv>
    </>
  );    
};

export default Sort;
