import styled from "styled-components";
import    {ReactComponent as add} from "./add.svg"
import burger from './Navbar'

export const StyledNavbar = styled.nav`
  margin: 0;
  height: 64px;
  display: flex;
  justify-content: space-between;
  color: black;
  background: #022541;
  font-family: 'Source Sans Pro', Arial, sans-serif;
  /* @media (max-width: 768px) {
    flex-direction: column;
  } */
`;

export const StyledContainer = styled.div`
  height: 100%;
  display: flex;

  & ul {
    display: flex;
    list-style-type: none;
    gap: 1rem;
    color: white;
    margin: auto 0;
    padding-left: 2rem;
    font-family: "Source Sans Pro", Arial, sans-serif;
    font-size: 1em;
  }
    @media (max-width: 768px) {
    display:none;
  }
`;

export const RightContainer=styled.div`
  font-family: 'Source Sans Pro', Arial, sans-serif;
height: 100%;
display: flex;
justify-content: space-between;
    gap: 2rem;
    margin: 0 3rem 0 0;
    @media (max-width: 768px) {
    display:none;
  }
`;

export const HeaderImage = styled.div`
  height: 100%;
  width: 154px;
  display: flex;
  align-items: center;
  margin-left: 2.5rem;

`;

export const Add=styled(add)`
width: 2rem;
`;

export const EnButton=styled.button`
  font-family: 'Source Sans Pro', Arial, sans-serif;
background-color: transparent;
border:1px solid white;
color: white;
font-size: 0.75em;
font-weight: 400;
width: 28px;
    height: 26px;
    margin: auto;
    border-radius:3px;

padding:0;
&:hover{
background-color: white;
color :#022541;
cursor: pointer;
}
`;

export const Links=styled.a`
  font-family: 'Source Sans Pro', Arial, sans-serif;
color: white;
padding: auto;
margin: auto;
font-weight: 600;
font-size: 1em;
`;

export const BurgerButton=styled.button`
background-image: url('/burger.svg');
width: 50px;



`
