import styled from "styled-components";

const ButtonDiv = styled.button`
  width: 100%;
  height: 43px;
  display: flex;
  border-radius: 40px;
  align-items: center;
  justify-content: center;
  align-content: center;
  color: #767676;
  background-color: ${(probs) => (probs.disabled ? "#ECECEC" : "#14AAE9")};
  margin: 15px 0 0 0;

  & h2 {
    font-size: 1.3em;
  }
`;

export default ButtonDiv;
