import { SearchList, SortDiv ,Arrow} from "./styles";

const Sort = () => {
  const [rotate,setRotate]=useState('0');
  const clickHandler=()=>{
  if(rotate==='90'){
    setRotate('0')
  }else{
setRotate('90');
  }}
  return (

      <>  
    <SortDiv>
     <h2>Popular Moives</h2>
      <SearchList onClick={clickHandler}><h2>Sort</h2>   <Arrow rotateDeg={rotate}></Arrow></SearchList>
      <SearchList> <h2>Filter</h2> <Arrow></Arrow></SearchList>
      <SearchList><h2>SearchList</h2> <Arrow ></Arrow></SearchList>
    </SortDiv>
    </>
  );    
};

export default Sort;
