import styled from "styled-components";

export const MenuDiv = styled.div`
  flex-direction: column;
  flex-wrap: wrap;
  display: ${(props) => (props.isClicked ? "flex" : "none")};
  position: absolute;
  background-color: gr;
`;

export const Menu = styled.div``;
