import styled from "styled-components";

export const ButtonDiv = styled.button`
  width: 100%;
  height: 45px;
  display: flex;
  border: 1px solid black;
  border-radius: 20px;
  align-items: center;
  justify-content: center;
  align-content: center;
  color: white;
  background-color: ${(probs) => (probs.disabled ? "grey" : "blue")};
  margin: 15px 0 0 0;
`;
