import React, { useState }  from "react";
import Select from "../UI/Select/Select";
import { SearchList, SortDiv ,Arrow} from "./styles";


const Sort = () => {
  const [radius,setRadius]=useState('8px')
  const [rotate,setRotate]=useState('0');
  const clickHandler=()=>{
  if(rotate==='90deg'){
    setRotate('0deg')
    setRadius
  }else{
setRotate('90deg');

  }}
  return (

      <>  
    <SortDiv>
     <h2>Popular Moives</h2>
      <SearchList onClick={clickHandler} radius={radius}><h2>Sort</h2>   <Arrow rotate={rotate} />  </SearchList>
      {rotate==='90deg'  &&  <Select></Select>}

      <SearchList> <h2>Filter</h2> <Arrow /></SearchList>
      <SearchList><h2>SearchList</h2> <Arrow  /></SearchList>
    </SortDiv>
    </>
  );    
};

export default Sort;
