import styled from 'styled-components';

const MainDiv = styled.div`
  display: flex;
  flex-direction: column;
    margin: 0;
    padding: 0;



  @media (min-width: 768px) {
    flex-direction: column;
    margin: auto;
  padding: 30px;
  }
`;
export default MainDiv;
