import styled from 'styled-components';

export const CardContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  padding: 1rem;
  @media (max-width: 768px) {
    flex-direction: row;
    width: auto;
  }
`;

export const CardDiv = styled.div`
  border-radius: 8px;
  border: 1px solid #e3e3e3;
  border: 1px solid #e3e3e3;
  box-shadow: 0 2px 8px rgb(0 0 0 / 10%);
  display: flex;
  flex-direction: column;
  height: 336px;
  margin-top: 30px;
  position: relative;
  width: 155px;
  @media (max-width: 768px) {
    flex-direction: row;
    height: 141px;
    width: 100%;
  }
`;

export const CardImage = styled.div`

  @media (min-width: 768px) {
    border-radius: 8px 0 0 0;
    flex-direction: row;
    height: 141px;
    min-height: 141px;
    width: 94px;
  }
`;

export const CardInfo = styled.div`
    flex-direction: row;
  @media (min-width: 768px) {
    flex-direction: column;
  flex-wrap: wrap;
  overflow-wrap: break-word;
  width: 100%;
  word-wrap: normal;

  }
`;

export const Title = styled.h2`
  font-size: 0.9rem;
  margin-left: 5px;
  white-space: normal;
  @media (max-width: 768px) {
    margin: 10px;
  }
`;

export const ReleasedDate = styled.p`
  margin: 10px;
  @media (min-width: 768px) {
    color: rgba(0, 0, 0, 0.6);
    font-size: 0.5rem;
    font-size: 1em;
    margin-left: 5px;
    padding: 0;
  }
`;

export const OverView = styled.p`
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 2;
  display: -webkit-box;
  font-size: 0.8em;
  line-height: 21px;
  margin: 10px;
  max-height: 48px;
  overflow: hidden;
  padding: 0;
  text-overflow: ellipsis;
  @media (min-width: 768px) {
    display: none;
  }
`;
