import styled from "styled-components";
import    {ReactComponent as add} from "./add.svg"

export const StyledNavbar = styled.nav`
  margin: 0;
  height: 64px;
  display: flex;
  justify-content: space-between;
  color: black;
  background: #022541;
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

export const StyledContainer = styled.div`
  height: 100%;
  display: flex;

  & ul {
    display: flex;
    list-style-type: none;
    gap: 1rem;
    color: white;
    margin: auto 0;
    padding-left: 2rem;
    font-family: "Source Sans Pro", Arial, sans-serif;
    font-size: 1em;
  }
`;

export const RightContainer=styled.div`
height: 100%;
display: flex;

& div{

  width: 28px;
    height: 26px;
    display: flex;
    justify-content: center;
    align-items: center;
    align-content: center;
    border: 1px solid #fff;
    border-radius: 3px;
    color: #fff;
    font-weight: 600;
    font-size: .8em;
}

& ul {
  display: flex;
  list-style-type: none;
  gap: 2rem;
  color: white;
  margin: auto 0;
  padding-right: 2rem;
  font-family: "Source Sans Pro", Arial, sans-serif;
  font-size: 1em;
}
&:hover {
  ul {
cursor: pointer;  
}

}
`;

export const HeaderImage = styled.div`
  height: 100%;
  width: 154px;
  display: flex;
  align-items: center;
  margin-left: 2.5rem;

`;

export const Add=styled(add)`
width: 2rem;
`;

export const EnButton=styled.button`
background-color: transparent;
border:1px solid white;
color: white;
font-size: 0.7  em;
font-weight: 400;
width: 28px;
    height: 26px;
    margin: auto;
    border-radius:3px;

padding:0;
&:hover{
background-color: white;
color :#022541;
}
`;
