  const [rotate,setRotate]=useState('');
  const clickHandler=(probs)=>{
  if(rotate==='90'){
    setRotate('0')
  }



  }


  return (
      <>

    <SortDiv>
     <h2>Popular Moives</h2>
      <SearchList onClick={clickHandler}><h2>Sort</h2>   <Arrow></Arrow></SearchList>
      <SearchList> <h2>Filter</h2> <Arrow></Arrow></SearchList>
      <SearchList><h2>SearchList</h2> <Arrow rotate={clickHandler}></Arrow></SearchList>
    </SortDiv>
    </>
  );    
};

export default Sort;
